def generate_sha256_pkce(length: int) -> Tuple[str, str]:
    if not (43 <= length <= 128):
        raise Exception("Invalid length: " % str(length))
    verifier = secrets.token_urlsafe(length)
    encoded = base64.urlsafe_b64encode(hashlib.sha256(verifier.encode('ascii')).digest())
    challenge = encoded.decode('ascii')[:-1]
    return verifier, challenge

def registerApp() -> Tuple[str, str]:
    request = {
        "redirect_uris":  ["http://127.0.0.1:4711/SensorApp"],
        "audiences": ["3042e50b-dc09-4817-b34c-1b06c709da78"],
        "grant_types": ["authorization_code", "offline_access"],
        "response_types": ["code", "code id_token"],
        "client_name": "STAplus SCK App",
        "logo_uri": "data:image/png;base64,",
        "scope": "openid profile email idp offline_access citiobs.secd.eu#create, citiobs.secd.eu#update",
        "contacts": [
          "Secure Dimensions GmbH",
          "https://www.secure-dimensions.de",
          "W 28",
          "DE",
          "https://www.secure-dimensions.de/legal"
        ],
        "operator_country": "de",
        "tos_uri": "https://www.secure-dimensions.de/terms",
        "policy_uri": "https://www.secure-dimensions.de/privacy",
        "software_id": "b8815b0ff48b66ed3adbecb5d405fb15d941dbdb",
        "software_version": "1.0",
        "application_type": "web"
    }

    register = False
    app_metadata = {}
    try:
        with open('SensorApp.json', 'r') as f:
            app_metadata = json.loads(f.read())
            if int(time.time()) >= app_metadata['expires']:
                register = True
    except FileNotFoundError:
                register = True

    if register:
        header = {'Content-Type': 'application/json', 'Accept': 'application/json'}
        response = requests.post('https://www.authenix.eu/oauth/register', data=json.dumps(request), headers=header)
        if response.status_code != 200:
            _logger.error("App Registration error: ", response.content)

        app_metadata = response.json()
        with open('SensorApp.json', 'w') as f:
            json.dump(app_metadata, f)

    client_id = app_metadata['client_id']
    client_secret = app_metadata['client_secret']

    _logger.info("client_id :%s", client_id)
    _logger.info("client_secret :%s", client_secret)
    return client_id, client_secret

def authorize():
    scopes = ['openid', 'profile', 'citiobs.secd.eu#create', 'citiobs.secd.eu#update']
    redirect_uri = 'http://127.0.0.1:4711/SensorApp'
    (client_id, client_secret) = registerApp()
    state = ''.join(random.choice(string.ascii_letters) for m in range(10))

    service_information = ServiceInformation('https://www.authenix.eu/oauth/authorize',
                                             'https://www.authenix.eu/oauth/token',
                                             client_id,
                                             client_secret,
                                             scopes)
    manager = CredentialManager(service_information)

    code_verifier, code_challenge = generate_sha256_pkce(64)
    # Builds the authorization url and starts the local server according to the redirect_uri parameter
    kwargs = {'response_type': 'id_token code'}
    url = manager.init_authorize_code_process(redirect_uri,
                                         state,
                                         code_challenge=code_challenge,
                                         code_challenge_method="S256",
                                         **kwargs)
    _logger.info('Open this url in your browser\n%s', url)
    webbrowser.open(url, new=0, autoraise=True)

    code, id_token = manager.wait_and_terminate_authorize_code_process()

    _logger.debug('code got = %s', code)
    _logger.debug('id_token got = %s', id_token)
    kid = "www.authenix.eu#id_token"
    url = "https://www.authenix.eu/.well-known/jwks.json"
    jwks_client = PyJWKClient(url)
    signing_key = jwks_client.get_signing_key_from_jwt(id_token)
    data = jwt.decode(
        id_token,
        signing_key.key,
        algorithms=["RS256"],
        audience=client_id,
        options={"verify_exp": False, 'verify_iat': False},
    )
    _logger.info(data)
    manager.init_with_authorize_code(redirect_uri, code)
    _logger.debug('Access Token = %s', manager._access_token)
    # Here access and refresh token may be used with self.refresh_token

    return manager._access_token, data

def connect_mqtt(token):
    def on_connect(client, userdata, flags, rc):
        if rc == 0:
            print("Connected to MQTT Broker!")
        else:
            print("Failed to connect, return code %d\n", rc)
    # Set Connecting Client ID
    client = mqtt_client.Client(client_id)
    client.username_pw_set('Bearer', token)
    client.on_connect = on_connect
    client.connect(broker, port)
    return client


def publish(service, client, ids):
    thing = service.things().query().filter("id eq '" + ids.get('thing_id') + "'") #.expand("Locations").list().get(0)
    thing.locations = [location]
    lon = location.location['coordinates'][0]
    lat = location.location['coordinates'][1]

    foi = None
    fois = service.features_of_interest().query().list()
    if fois.entities:
        for f in fois.entities:
            if 'coordinates' in f.feature.keys() and f.feature['coordinates'][0] == lon and f.feature['coordinates'][1] == lat:
                foi = f
                break
    if foi is None:
        p = Point((lon, lat))
        foi = frost_sta_client.model.feature_of_interest.FeatureOfInterest(location.name, location.description, 'application/geo+json', p)
        service.create(foi)

    now = datetime.now(timezone.utc).replace(microsecond=0).isoformat()
    temperature = staPlus.Observation(None, None, now)
    temperature.feature_of_interest = foi
    temperature.datastream = service.datastreams().find(ids.get('temp_id'))

    humidity = staPlus.Observation(None, None, now)
    humidity.feature_of_interest = foi
    humidity.datastream = service.datastreams().find(ids.get('humidity_id'))

    light = staPlus.Observation(None, None, now)
    light.feature_of_interest = foi
    light.datastream = service.datastreams().find(ids.get('light_id'))

    noise = staPlus.Observation(None, None, now)
    noise.feature_of_interest = foi
    noise.datastream = service.datastreams().find(ids.get('noise_id'))

    pressure = staPlus.Observation(None, None, now)
    pressure.feature_of_interest = foi
    pressure.datastream = service.datastreams().find(ids.get('pressure_id'))

    pm1 = staPlus.Observation(None, None, now)
    pm1.feature_of_interest = foi
    pm1.datastream = service.datastreams().find(ids.get('pm1_id'))

    pm25 = staPlus.Observation(None, None, now)
    pm25.feature_of_interest = foi
    pm25.datastream = service.datastreams().find(ids.get('pm25_id'))

    pm10 = staPlus.Observation(None, None, now)
    pm10.feature_of_interest = foi
    pm10.datastream = service.datastreams().find(ids.get('pm10_id'))

    sck.write('shell -on\nmonitor -noms Temperature,Humidity,Light,Noise dBA,Barometric pressure,PM 1.0,PM 2.5,PM 10.0\n'.encode('ASCII'))
    while True:
        data = None
        while sck.in_waiting:
            data = sck.readline().decode('utf-8')
            # print(data)
        if data is not None and not data.startswith('SCK'):
            print(data)
            obs = list(filter(None, data.split()))
            if len(obs) != 9:
                continue

            (d, t, h, l, n, p, v_pm1, v_pm25, v_pm10) = obs

            now = datetime.now(timezone.utc).replace(microsecond=0).isoformat()
            if d == "0":
                d = now

            temperature.phenomenon_time = d
            temperature.result_time = now
            temperature.result = float(t)

            humidity.phenomenon_time = d
            humidity.result_time = now
            humidity.result = float(h)

            light.phenomenon_time = d
            light.result_time = now
            light.result = float(l)

            noise.phenomenon_time = d
            noise.result_time = now
            noise.result = float(n)

            pressure.phenomenon_time = d
            pressure.result_time = now
            pressure.result = float(p)

            pm1.phenomenon_time = d
            pm1.result_time = now
            pm1.result = float(v_pm1)

            pm25.phenomenon_time = d
            pm25.result_time = now
            pm25.result = float(v_pm25)

            pm10.phenomenon_time = d
            pm10.result_time = now
            pm10.result = float(v_pm10)

            print('publishing...')
            client.publish("v1.1/Observations", json.dumps(transform_entity_to_json_dict(temperature)))
            client.publish("v1.1/Observations", json.dumps(transform_entity_to_json_dict(humidity)))
            client.publish("v1.1/Observations", json.dumps(transform_entity_to_json_dict(light)))
            client.publish("v1.1/Observations", json.dumps(transform_entity_to_json_dict(pressure)))
            client.publish("v1.1/Observations", json.dumps(transform_entity_to_json_dict(noise)))
            client.publish("v1.1/Observations", json.dumps(transform_entity_to_json_dict(pm1)))
            client.publish("v1.1/Observations", json.dumps(transform_entity_to_json_dict(pm25)))
            client.publish("v1.1/Observations", json.dumps(transform_entity_to_json_dict(pm10)))
            print('done.')

        time.sleep(10)

